package com.dss.praticalprogressimport android.app.Activityimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.view.WindowManagerclass GlobalProgressBuilder {    private var progress: View? = null    private lateinit var targetView: View    private var progressLayout: Int = 0    private lateinit var activity: Activity    fun with(activity: Activity): GlobalProgressBuilder {        this.activity = activity        targetView = activity.findViewById<View>(android.R.id.content).rootView        return this    }    fun setCustomLayout(layoutId: Int): GlobalProgressBuilder {        progressLayout = layoutId        return this    }    fun load(): GlobalProgressBuilder {        progressLayout = if (progressLayout == 0) R.layout.dialog_progress_global else progressLayout        createGlobalProgress()        return this    }    fun finishLoad(): GlobalProgressBuilder {        DialogUtils.checkRemoveProgress(targetView,activity)        return this    }    private fun createGlobalProgress() {        val inflater = LayoutInflater.from(activity)        val progress: View =            inflater.inflate(R.layout.dialog_progress_global, targetView as ViewGroup, true)        this.progress = progress        activity.window.setFlags(            WindowManager.LayoutParams.FLAG_NOT_TOUCHABLE,            WindowManager.LayoutParams.FLAG_NOT_TOUCHABLE        )    }}